CREATE TABLE `gym`.`users` ( `id` INT NOT NULL AUTO_INCREMENT , `name` VARCHAR(30) NOT NULL , `surname` VARCHAR(30) NOT NULL , `email` VARCHAR(60) NOT NULL , `password` TEXT NOT NULL , `phone` INT NOT NULL , `address` TEXT NOT NULL , `created_at` DATETIME on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;